import numpy as np
import matplotlib as mpl
import matplotlib.pyplot as plt
mpl.use('Agg')

all_data = {'pretrain_G_losses':
     [0.005652431311900728, 0.0011757896953495219, 0.0008108276000712067, 0.0006562282050435897, 0.0005278110297513195, 0.0004473166276293341, 0.0003752061084669549, 0.00030063697320292706, 0.0003118004955613287, 0.00029363731926423497]
 }

def plot_pretrain(all_data):
    data_num = len(all_data)
    pretrain_G_losses = all_data['pretrain_G_losses']

    print(pretrain_G_losses)
    epochs = len(pretrain_G_losses)

    x = np.array([i for i in range(epochs)])
    pretrain_G_losses = np.array(pretrain_G_losses)
    max_loss = max(pretrain_G_losses)

    plt.title('pretrain G loss')

    plt.plot(x, pretrain_G_losses, color='cyan', label='loss')
    # plt.legend()
    plt.xlabel('epoch')
    plt.ylabel('loss')
    plt.axis([0, epochs+5, 0, max_loss])
    plt.savefig('pretrian_loss_curves')
    plt.show()
    plt.cla()
    plt.clf()
    plt.close()

if __name__ == "__main__":
    all_data = {'pretrain_G_losses':
                    [0.005652431311900728, 0.0011757896953495219, 0.0008108276000712067, 0.0006562282050435897,
                     0.0005278110297513195, 0.0004473166276293341, 0.0003752061084669549, 0.00030063697320292706,
                     0.0003118004955613287, 0.00029363731926423497]
                }
    plot_pretrain(all_data)



